import React, { useEffect, useState } from 'react';

export default function AdminPanel() {
  const [users, setUsers] = useState([]);
  const [editing, setEditing] = useState(null);
  const [message, setMessage] = useState('');

  const load = async () => {
    try {
      const res = await fetch('http://localhost:1234/api/admin/users', {
        credentials: 'include'
      });
      const data = await res.json();

      if (res.ok) setUsers(data.users);
      else setMessage(data.error || 'Error fetching users');
    } catch (err) {
      setMessage('Server unreachable');
    }
  };

  useEffect(() => {
    load();
  }, []);

  const remove = async (id) => {
    if (!confirm('Delete this user?')) return;

    const res = await fetch(`http://localhost:1234/api/admin/users/${id}`, {
      method: 'DELETE',
      credentials: 'include'
    });

    if (res.ok) load();
    else alert('Error deleting user');
  };

  const startEdit = (u) => setEditing({ ...u, password: '' });

  const saveEdit = async () => {
    const id = editing._id || editing.id;

    const body = {
      username: editing.username,
      email: editing.email,
      role: editing.role
    };
    if (editing.password) body.password = editing.password;

    const res = await fetch(`http://localhost:1234/api/admin/users/${id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      credentials: 'include',
      body: JSON.stringify(body)
    });

    const data = await res.json();

    if (res.ok) {
      setEditing(null);
      load();
    } else {
      alert(data.error || 'Error saving user');
    }
  };

  return (
    <div className="card">
      <h2>Admin â€” Users</h2>

      {message && <div className="error">{message}</div>}

      <table className="users-table">
        <thead>
          <tr><th>Username</th><th>Email</th><th>Role</th><th>Actions</th></tr>
        </thead>
        <tbody>
          {users.map(u => (
            <tr key={u._id}>
              <td>{u.username}</td>
              <td>{u.email}</td>
              <td>{u.role}</td>
              <td>
                <button onClick={() => startEdit(u)}>Edit</button>
                <button className="danger" onClick={() => remove(u._id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {editing && (
        <div className="edit-panel">
          <h3>Edit user</h3>

          <label>Username
            <input value={editing.username} onChange={e => setEditing({ ...editing, username: e.target.value })} />
          </label>

          <label>Email
            <input value={editing.email} onChange={e => setEditing({ ...editing, email: e.target.value })} />
          </label>

          <label>Role
            <select value={editing.role} onChange={e => setEditing({ ...editing, role: e.target.value })}>
              <option value="user">user</option>
              <option value="admin">admin</option>
            </select>
          </label>

          <label>New password (optional)
            <input type="password" value={editing.password} onChange={e => setEditing({ ...editing, password: e.target.value })} />
          </label>

          <div className="actions">
            <button onClick={saveEdit}>Save</button>
            <button onClick={() => setEditing(null)}>Cancel</button>
          </div>
        </div>
      )}
    </div>
  );
}
